    if (this.contentRef) {
      this.props.onModalWillHide && this.props.onModalWillHide();
      this.contentRef[animationOut](this.props.animationOutTiming).then(() => {
        this.isTransitioning = false;
        if (this.props.isVisible) {
          this.open();
        } else {
          this.setState(
            {
              showContent: false,
            },
            () => {
              this.setState(
                {
                  isVisible: false,
                },
                () => {
                  this.props.onModalHide();
                }
              );
            }
          );
        }
      });
    }
  };

  render() {
    const {
      animationIn,
      animationInTiming,
      animationOut,
      animationOutTiming,
      avoidKeyboard,
      coverScreen,
      hasBackdrop,
      backdropColor,
      backdropOpacity,
      backdropTransitionInTiming,
      backdropTransitionOutTiming,
      customBackdrop,
      children,
      deviceHeight: deviceHeightProp,
      deviceWidth: deviceWidthProp,
      isVisible,
      onModalShow,
      onBackdropPress,
      onBackButtonPress,
      useNativeDriver,
      propagateSwipe,
      style,
      ...otherProps
    } = this.props;
    const deviceWidth = deviceWidthProp || this.state.deviceWidth;
    const deviceHeight = deviceHeightProp || this.state.deviceHeight;

    const computedStyle = [
      { margin: deviceWidth * 0.05, transform: [{ translateY: 0 }] },
      styles.content,
      style,
    ];

    let panHandlers = {};
    let panPosition = {};
    if (this.state.isSwipeable) {
      panHandlers = { ...this.panResponder.panHandlers };

      if (useNativeDriver) {
        panPosition = {
          transform: this.state.pan.getTranslateTransform(),
        };
      } else {
        panPosition = this.state.pan.getLayout();
      }
    }

    const _children =
      this.props.hideModalContentWhileAnimating &&
      this.props.useNativeDriver &&
      !this.state.showContent ? (
        <animatable.View />
      ) : (
        children
      );
    const containerView = (
      <animatable.View
        {...panHandlers}
        ref={ref => (this.contentRef = ref)}
        style={[panPosition, computedStyle]}
        pointerEvents="box-none"
        useNativeDriver={useNativeDriver}
        {...otherProps}
      >
        {_children}
      </animatable.View>
    );

    const hasCustomBackdrop = React.isValidElement(customBackdrop);

    const backdropContent = (
      <animatable.View
        ref={ref => (this.backdropRef = ref)}
        useNativeDriver={useNativeDriver}
        style={[
          styles.backdrop,
          {
            width: deviceWidth,
            height: deviceHeight,
          },
          {
            backgroundColor:
              this.state.showContent && !hasCustomBackdrop
                ? backdropColor
                : 'transparent',
          },
        ]}
